import { makeStyles } from '@material-ui/core';


export default makeStyles ({
    media: {
        height:200,
},

card: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    borderBottom: '10px solid white',
},
activeCard: {
    borderBottom: '8px solid #22289a',  
},

grid: {
    display: 'flex',

},

details: {
    display: 'flex',
